package day_01;

public class fibonaccis {
	
   static int number,number1,number2;
	
	static int fibo(int number) {
		
	 for (number=0;number<=100;number=number1+number2)
	 {
		 
		 System.out.println("The fibonacci series:"+number); 
	 }
	 
	 return number;
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
	     fibo(number);
	}

}
